import React, { Component } from "react";
import List from "./List";
import './Search.css';


export default class Search extends Component {
  constructor(props) {
    super(props);
    console.log("constructor: It's default time!");
    
    this.state = {
      places: [],
      matches: [],
      inputValue: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleClearClick = this.handleClearClick.bind(this);
  }

  /* DO NOT MODIFY */
  componentDidMount() {
    const allPlaces = [];
    const endpoint =
      "https://gist.githubusercontent.com/Miserlou/c5cd8364bf9b2420bb29/raw/2bf258763cdddd704f8ffd3ea9a3e81d25e2c6f6/cities.json";
    fetch(endpoint)
      .then(data => data.json())
      .then(results => {
        allPlaces.push(...results)
        console.log("fetched the endpoint in componentDidMount()")
        this.setState({ places: allPlaces })
      })
      .catch(error => console.log(error));
  }

  handleChange(event) {
    console.log('handling change')
    this.setState({inputValue: event.target.value})
    const clearIcon = document.querySelector(".clear-icon");
    const resultList = document.querySelector(".list-wrapper");
    const header = document.querySelector(".header");
    if(event.target.value === "") {
      clearIcon.style.visibility = "hidden";
      resultList.style.visibility = "hidden";
      header.style.visibility = "hidden";
      
      
    } else {
      console.log("finding matches")
      header.style.visibility = "visible";
      clearIcon.style.visibility = "visible";
      resultList.style.visibility = "visible";

      const regex = new RegExp(event.target.value.toString())
      const currMatches = this.state.places.filter(place => 
      regex.test(place.city) || regex.test(place.state)
      );
      this.setState({matches: currMatches});
    }
    
    
  }

  handleClearClick(e) {
    e.preventDefault()
    this.setState({inputValue: ""})
    this.setState({matches: []})
    document.querySelector(".search").value = "";
    document.querySelector(".clear-icon").style.visibility = "hidden";
    document.querySelector(".header").style.visibility = "hidden";

  }


  render() {
    console.log("rendering the Search (parent) component")
    return (
      <>
        <div className="search-wrapper">
          <img 
            className="search-icon" 
            alt="search-pic" 
            src="data:image/svg+xml;utf8;base64," 
          />
            <input 
              type="text" 
              id="finder"
              className = "search"
              placeholder="Search for a City or State" 
              onChange={this.handleChange} 
            />
          <img 
            className="clear-icon" 
            onClick={this.handleClearClick} 
            onFocus={e => e.stopPropagation()} 
            onMouseDown={e => e.preventDefault()}
            alt="clear" 
            src="data:image/svg+xml;utf8;base64," />
        </div>
        <h4 className = "header">RESULTS</h4>
        <div className="list-wrapper">
            <List matches={this.state.matches}/>
        </div>
      </>
    )
  }
}


